import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'frontend';
  images = '';
  imgURL ='assets/noimage.png';

  constructor(private http:HttpClient){}

  ngOnInit() {

    }

    selectImage(event:any) {
      if (event.target.files.length > 0) {
        const file = event.target.files[0];
        const reader = new FileReader();
         reader.readAsDataURL(file);
         reader.onload = (event: any)=>{
           this.imgURL = event.target.result;
         }
        this.images = file;
      }



      function selectImage() {
        throw new Error('Function not implemented.');
    }
  }
  onSubmit(){
    const formData = new FormData();
    formData.append('file',this.images);
    this.http.post<any>('http://localhost:3000/file', formData).subscribe(
      (res) => console.log(res,  Swal.fire({
                icon: 'success',
                title: 'Inmueble cargada!!',
                text: 'El Inmueble se subio correctamente!'
                }).then((result) => {
                            if (result) {
                                       location.reload();
                          }
               })
         ),
      (err) => Swal.fire({
                      icon: 'error',
                      title: 'Oops...',
                      text: 'Parece que no subio nada!!'
                    })
    );
   this.imgURL = '/assets/noimage.png';

    }
  }


<h1 class="text-center">Lista de Inmuebles</h1>
<hr>
<div>
  <div class="container">
    <div class="row">
      <div class="col-md-6">
        <div class="card">
          <div class="card-body">
            <form (ngSubmit)="onSubmit()">
              <div class="form-group">
                <label>Elija el inmueble a subir</label>
                <input class="form-control" type="file" name="image" accept ="image/*" (change)="selectImage($event)"><br>
                <button class="btn btn-outline-primary" type="submit">Cargar</button>
              </div>
            </form>
          </div>
        </div>
      </div>

    </div>
    <div class="col-md-6">
       <div class="card">
        <img class="card-img-top" [src]="imgURL">
       </div>
    </div>
  </div>
</div>
