import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'frontend';
  images = '';
  imgURL ='assets/noimage.png';
  imagenes:any = [];
  nombre: string = '';
  descripcion: string = '';
  precio: number = 0;
  ubicacion: string = '';
  metraje: number = 0;
  tipo_venta: string = '';
  tipo_inmueble: string = '';

  constructor(private http:HttpClient){}

  ngOnInit():void {
    this.mostrarImg();
    }

    selectImage(event:any) {
      if (event.target.files.length > 0) {
        const file = event.target.files[0];
        const reader = new FileReader();
         reader.readAsDataURL(file);
         reader.onload = (event: any)=>{
           this.imgURL = event.target.result;
         }
        this.images = file;
      }



      function selectImage() {
        throw new Error('Function not implemented.');
    }
  }
  onSubmit(){
    const formData = new FormData();
    formData.append('nombre', this.nombre);
    formData.append('descripcion', this.descripcion);
    formData.append('precio', this.precio.toString());
    formData.append('ubicacion', this.ubicacion);
    formData.append('metraje', this.metraje.toString());
    formData.append('tipo_venta', this.tipo_venta);
    formData.append('tipo_inmueble', this.tipo_inmueble);
    formData.append('file',this.images);
    this.http.post<any>('http://localhost:3000/file', formData).subscribe(
      (res) => console.log(res,  Swal.fire({
                icon: 'success',
                title: 'Inmueble cargado!!',
                text: 'El Inmueble se subio correctamente!'
                }).then((result) => {
                            if (result) {
                                       location.reload();
                          }
               })
         ),
      (err) => Swal.fire({
                      icon: 'error',
                      title: 'Oops...',
                      text: 'Parece que no subio nada!!'
                    })
    );
   this.imgURL = '/assets/noimage.png';

    }

    mostrarImg(){
      this.http.get<any>('http://localhost/imagenes').subscribe(res=>{
       this.imagenes=res;
       const reader = new FileReader();
       reader.onload = this.imagenes;
       console.log(this.imagenes);
      });



    }


  }



<h1 class="text-center">Crear Inmueble</h1>
<hr>
<div class="container">
  <div class="row">
    <div class="col-md-6">
      <div class="card">
        <img class="card-img-top" [src]="imgURL">
      </div>
    </div>
    <div class="col-md-6">
      <div class="card">
        <div class="card-body">
          <form (ngSubmit)="onSubmit()">
            <div class="form-group">
              <label>Nombre:</label>
              <input class="form-control" type="text" name="nombre" [(ngModel)]="nombre">
            </div>
            <div class="form-group">
              <label>Descripción:</label>
              <textarea class="form-control" name="descripcion" [(ngModel)]="descripcion"></textarea>
            </div>
            <div class="form-group">
              <label>Precio:</label>
              <input class="form-control" type="number" name="precio" [(ngModel)]="precio">
            </div>
            <div class="form-group">
              <label>Ubicación:</label>
              <input class="form-control" type="text" name="ubicacion" [(ngModel)]="ubicacion">
            </div>
            <div class="form-group">
              <label>Metraje:</label>
              <input class="form-control" type="number" name="metraje" [(ngModel)]="metraje">
            </div>
            <div class="form-group">
              <label>Tipo de venta:</label>
              <select class="form-control" name="tipo_venta" [(ngModel)]="tipo_venta">
                <option value="venta">Venta</option>
                <option value="alquiler">Alquiler</option>
              </select>
            </div>
            <div class="form-group">
              <label>Tipo de inmueble:</label>
              <select class="form-control" name="tipo_inmueble" [(ngModel)]="tipo_inmueble">
                <option value="casa">Casa</option>
                <option value="departamento">Departamento</option>
                <!-- Agrega más opciones si es necesario -->
              </select>
            </div>
            <div class="form-group">
              <label>Elija el inmueble a subir</label>
              <input class="form-control" type="file" name="image" accept ="image/*" (change)="selectImage($event)"><br>
              <button class="btn btn-outline-primary" type="submit">Cargar</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>

<hr>

<h2 class="text-center">Lista de Inmuebles</h2>
<div class="container">
  <div class="row">
    <div class="col-md-4" *ngFor="let images of this.imagenes">
      <div class="card">
        <div class="card-header bg-dark text-white d-flex justify-content-between align-items">
           {{images.id}},{{images.nombre}},{{images.descripcion}},{{images.precio}},{{images.ubicacion}},{{images.metraje}},{{images.tipo_venta}},{{images.tipo_inmueble}}
        </div>
        <div class="card-body">

          <img class="" src="http://localhost:3000/{{images.images}}">
        </div>
        <button class="btn btn-outline-danger btn-block">Eliminar</button>
      </div>
    </div>
  </div>
</div>
